import {React, useState} from 'react';
import $ from "jquery";
import Axios from "axios";

const Registration = () => {
    const [name, changeName] = useState('')
    const submitForm = (e) => {
        e.preventDefault()
        if ($('#reg')[0].value === "") {
            console.log('Invalid')
            return
        }
        $('#reg').prop("disabled", true);
        Axios.post('http://127.0.0.1:4567/registration', {
            reg: $("#reg")[0].value,
        }).then((res) => {
            console.log(res)
            $('#step-1').prop("hidden", true);
            $("#step-2").prop("hidden", false);
            changeName(res.data[0].name)
        })
    }
    return (
        <>
            <div className='container m-5' id='step-1'>
                <form>
                    <div className='mb-3'>
                        <label htmlFor="exampleInputEmail1" className="form-label">Registration Number</label>
                        <input type="text" className="form-control" id="reg"/>
                    </div>
                    <button type="submit" className="btn btn-primary" onClick={e => submitForm(e)}>Submit</button>
                </form>
            </div>
            <div className='container m-5' id='step-2' hidden={true}>
                <label htmlFor="exampleInputEmail1" className="form-label">Name</label>
                <input type='text' className='form-control'/>
                <label htmlFor="exampleInputEmail1" className="form-label">Registration Number</label>
                <input type='text' className='form-control'/>
            </div>
        </>
    );
};

export default Registration;
